import { Recordable } from "../../../types";
import { Ref, ComputedRef } from "vue";
/**
 * 列自动设置宽度
 *
 * ( 宽度 < minWidth ) ==> minWidth
 * ( minWidth < 宽度 < 300px )  ==> 宽度
 * ( 宽度 > 300px ) ==> 300px
 *
 * @param columns 表格列
 * @param dataSource 表格数据
 */
export default function useAutoColsWidth(columns: ComputedRef<Recordable[]>, dataSource: Ref<any[]>): void;
